
const decode = (token)=> {
    const d = JSON.parse(atob(token.split('.')[1]));
     const tokenData = Object.assign(    
        {
            exp:d.exp,
            iat:d.iat,
            id:d.id,
            name:d.name,
            roles: d.roles       
        }
      );
  return tokenData;
};


module.exports = {
    decode
};